/** @format */

import React, {useState} from "react";
import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import Card from "../UI/Card";
import ExpenseFilter from "../NewExpense/ExpenseFilter";

const items = ({ items }) => {

  const [filterYear, setFilterYear] = useState('2019')

  const addFilterYear = (selectedYear) => {
    setFilteYear(selectedYear);
     console.log(`Filter of the Expense from the ${selectedYear}`);
  };




  return (
    <div>

      <Card className="items">
        <ExpenseFilter selected={filterYear} onShowHanler={addFilterYear} />
        <ExpenseItem
          title={items[0].title}
          amount={items[0].amount}
          date={items[0].date}
        />

        <ExpenseItem
          title={items[1].title}
          amount={items[1].amount}
          date={items[1].date}
        />

        <ExpenseItem
          title={items[2].title}
          amount={items[2].amount}
          date={items[2].date}
        />

        <ExpenseItem
          title={items[3].title}
          amount={items[3].amount}
          date={items[3].date}
        />
      </Card>
    </div>
  );
};

export default items;
